<script>
export default {
  data(){
    return{
      flag: false,
      tosmall: false,
      isShow: false
    }
  },
  methods:{
    fold(){
      this.flag= !this.flag
      this.tosmall= !this.tosmall
      
    },
    unfold(){
      this.flag= !this.flag
      this.tosmall= !this.tosmall
    },
    backtotop(){
      document.documentElement.scrollTop = 0
    },
    handleScroll(){
     let top = document.documentElement.scrollTop
      if( top> 85){
        this.isShow = true
      } else {
        this.isShow = false
      }
    }    
  },
  mounted(){
    window.addEventListener("scroll", this.handleScroll)
  },
  destroyed(){
    window.removeEventListener("scroll", this.handleScroll)
  }
}
